<script>
import { Swiper, SwiperSlide } from "swiper/vue";
import "swiper/css";
import "swiper/css/pagination";
import "swiper/css/navigation";
import { Pagination, Navigation, Autoplay } from "swiper";
export default {
  components: {
    Swiper,
    SwiperSlide,
  },
  data() {
    return {
      currContent: 1,
      activeIndex1: 0,
      activeIndex2: 0,
      activeIndex3: 0,
      activeIndex4: 0,
      swiperRef1: null,
      swiperRef2: null,
      swiperRef3: null,
      swiperRef4: null,
      modules: [Pagination, Navigation, Autoplay],
    }
  },
  methods: {
    setSwiperRef1(swiper) {
      this.swiperRef1 = swiper;
    },
    setSwiperRef2(swiper) {
      this.swiperRef2 = swiper;
    },
    setSwiperRef3(swiper) {
      this.swiperRef3 = swiper;
    },
    setSwiperRef4(swiper) {
      this.swiperRef4 = swiper;
    },
    showContent(num) {
      this.currContent = num;
      this.$nextTick(() => {
        if(num == 1){
          this.swiperRef1.update();
          this.swiperRef1.slideTo(1, 0);
        } else if(num == 2){
          this.swiperRef2.update();
          this.swiperRef2.slideTo(1, 0);
        } else if(num == 3){
          this.swiperRef3.update();
          this.swiperRef3.slideTo(1, 0);
        }
      });
    },
    slideChange1(swiper) {
      this.activeIndex1 = swiper.realIndex;
    },
    slideChange2(swiper) {
      this.activeIndex2 = swiper.realIndex;
    },
    slideChange3(swiper) {
      this.activeIndex3 = swiper.realIndex;
    },
    slideChange4(swiper) {
      this.activeIndex4 = swiper.realIndex;
    },
  }
};
</script>
